import React, { useState, useEffect } from "react";

export default function App() {
  // fetch a list of the courses
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("./classes.json")
      .then((res) => res.json())
      .then((data) => {
        setData(data.classes); //change the state and re-render
      });
  }, []);

  let courses = data.map((course) => {
    let aSingleCourse = (
      <div className="col-sm-12 col-md-6 col-xl-4">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">{course.course}</h5>
            <h6 className="card-subtitle mb-2 text-muted">Card subtitle</h6>
            <p className="card-text">{course.description}</p>
          </div>
        </div>
      </div> 
    )

    return aSingleCourse;
  })

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col studyBuddy">STUDY BUDDY</div>
        <div className="col">Chats</div>
      </div>
      <div className="row">
        <div className="col">
          Select a class below to find classmates to study with:
        </div>
      </div>

      <div className="row justify-content-center">
        {courses}
      </div>
    </div>
  );
}
