import React from 'react';
import { useParams } from 'react-router-dom'
import { useState, useEffect } from "react";

export default function ClassPage(props) {
  const urlParams = useParams();

  // grab the course name from the URL params
  let courseName = urlParams.courseName;

  // fetch a list of the people in a course
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("./classSpecficPeople.json")
      .then((res) => res.json())
      .then((data) => {
        setData(data.people); //change the state and re-render
      });
  }, []);

  return (
    <div>
      Hello {courseName}
      <div className="col-sm-12 col-md-6 col-xl-4">
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">{props.course}</h5>
          <h6 className="card-subtitle mb-2 text-muted">{props.numStudents} students in this class</h6>
          <p className="card-text">{props.description}</p>
        </div>
      </div>
    </div> 

    </div>
  )
}
