import React from "react";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import StudentCard from './StudentCard';


export default function ClassPage(props) {
  const urlParams = useParams();

  // grab the course name from the URL params
  let courseName = urlParams.courseName;

  // fetch a list of the people in a course
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("./classSpecficPeople.json")
      .then((res) => res.json())
      .then((data) => {
        setData(data.people); //change the state and re-render
      });
  }, []);


  let students = data.map((course) => {
    return (
      <CourseCard
        course={course.course}
        numStudents={course.numStudents}
        description={course.description}
        key={course.id.toString()}
      />
    );
  });

  return (
    <div>
      Hello {courseName}
      <div className="col-9">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">YuYu Madigan</h5>
            <h6 className="card-subtitle mb-2 text-muted">
              Currently Enrolled
            </h6>
            <p className="card-text">Looking for: Homework Partners</p>
          </div>
        </div>
      </div>
    </div>
  );
}
