import React, { useState, useEffect } from "react";
import CourseCard from './CourseCard'
import { Route, Link, NavLink, Switch, Redirect } from 'react-router-dom';

export default function App() {
  // fetch a list of the courses
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("./classes.json")
      .then((res) => res.json())
      .then((data) => {
        setData(data.classes); //change the state and re-render
      });
  }, []);

  let courses = data.map((course) => {
    return <CourseCard 
      course={course.course}
      numStudents={course.numStudents}
      description={course.description} 
      key={course.id.toString()} 
    />
  })

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col studyBuddy">iSTUDY BUDDY</div>
        <div className="col chats">Chats</div>
      </div>

      <div className="row">
        <div className="col select">
          Select a class below to find classmates to study with:
        </div>
      </div>

      <div className="row justify-content-center">
      <Switch>
        <Route exact path="/" render={courses} />
        <Route path="/adopt/:petName" component={ClassPage} />
      </Switch>
{/* 
      <div className="row justify-content-center">
        {courses} */}
      </div>

      <div className="row justify-content-center">
        &copy; 2021 iStudy Buddy, YuYu Madigan, Kayla Lee, Saatvik Arya, Louis Ta
      </div>
    </div>
  );
}
